import React, { createContext, useContext, useState, useCallback } from 'react';

const SpreadsheetContext = createContext();

export const useSpreadsheet = () => useContext(SpreadsheetContext);

export const SpreadsheetProvider = ({ children }) => {
  const [cells, setCells] = useState(Array(1000).fill(''));
  const [history, setHistory] = useState([]);
  const [currentHistoryIndex, setCurrentHistoryIndex] = useState(-1);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCells, setSelectedCells] = useState([]);

  const updateCell = useCallback((index, value) => {
    setCells(prevCells => {
      const newCells = [...prevCells];
      newCells[index] = value;
      setHistory(prevHistory => [...prevHistory.slice(0, currentHistoryIndex + 1), newCells]);
      setCurrentHistoryIndex(prevIndex => prevIndex + 1);
      return newCells;
    });
  }, [currentHistoryIndex]);

  const undo = useCallback(() => {
    if (currentHistoryIndex > 0) {
      setCurrentHistoryIndex(prevIndex => prevIndex - 1);
      setCells(history[currentHistoryIndex - 1]);
    }
  }, [currentHistoryIndex, history]);

  const redo = useCallback(() => {
    if (currentHistoryIndex < history.length - 1) {
      setCurrentHistoryIndex(prevIndex => prevIndex + 1);
      setCells(history[currentHistoryIndex + 1]);
    }
  }, [currentHistoryIndex, history]);

  const value = {
    cells,
    updateCell,
    undo,
    redo,
    searchTerm,
    setSearchTerm,
    selectedCells,
    setSelectedCells,
  };

  return (
    <SpreadsheetContext.Provider value={value}>
      {children}
    </SpreadsheetContext.Provider>
  );
};
