import React, { useState } from 'react';
import { useSpreadsheet } from '../hooks/useSpreadsheet';

const Toolbar = () => {
  const { toggleNumericColumn } = useSpreadsheet();
  const [showDropdown, setShowDropdown] = useState(false);

  const columns = Array.from({ length: 26 }, (_, i) => String.fromCharCode(65 + i));

  const handleCheckboxChange = (column) => {
    toggleNumericColumn(column);
    alert(`Column ${column} is now accepting only numeric values.`);
  };

  return (
    <div className="p-4 border-b border-gray-300">
      <button
        onClick={() => setShowDropdown(!showDropdown)}
        className="bg-blue-500 text-white p-2 rounded"
      >
        Set Numeric Columns
      </button>
      {showDropdown && (
        <div className="absolute bg-white border rounded shadow-md p-2 mt-2">
          {columns.map((col) => (
            <div key={col} className="flex items-center">
              <input
                type="checkbox"
                id={`numeric-${col}`}
                onChange={() => handleCheckboxChange(col)}
                className="mr-2"
              />
              <label htmlFor={`numeric-${col}`}>{col}</label>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Toolbar;
