import React from 'react';
import Cell from './Cell';
import { useSpreadsheet } from '../hooks/useSpreadsheet';

const Grid = () => {
  const { cells, updateCell, searchTerm, currentPage, setCurrentPage, cellsPerPage } = useSpreadsheet();

  const filteredCells = Object.entries(cells)
    .filter(([_, cell]) => cell.value.toLowerCase().includes(searchTerm.toLowerCase()))
    .map(([cellId, cell]) => ({ ...cell, id: cellId }));

  const totalPages = Math.ceil(filteredCells.length / cellsPerPage);
  const paginatedCells = filteredCells.slice((currentPage - 1) * cellsPerPage, currentPage * cellsPerPage);

  return (
    <div>
      <div className="grid grid-cols-10 gap-0.5 p-4">
        {paginatedCells.map(({ value, format, validation, id }) => (
          <Cell
            key={id}
            value={value}
            format={format}
            validation={validation}
            onChange={(updates) => updateCell(id, updates)}
          />
        ))}
      </div>
      <div className="flex justify-center mt-4">
        <button 
          onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
          disabled={currentPage === 1}
          className="bg-blue-500 text-white px-4 py-2 rounded mr-2 disabled:bg-gray-300"
        >
          Previous
        </button>
        <span className="px-4 py-2">Page {currentPage} of {totalPages}</span>
        <button 
          onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
          disabled={currentPage === totalPages}
          className="bg-blue-500 text-white px-4 py-2 rounded ml-2 disabled:bg-gray-300"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default Grid;
