// components/Toolbar.js
import React from 'react';
import { useSpreadsheet } from '../hooks/useSpreadsheet';

const Toolbar = () => {
  const { formatCell } = useSpreadsheet();

  const handleFormatChange = (property, value) => {
    formatCell(property, value);
  };

  return (
    <div className="bg-gray-100 p-2 flex space-x-2 items-center">
      <div className="border-l border-gray-300 h-6 mx-2"></div>
      <select 
        onChange={(e) => handleFormatChange('fontSize', e.target.value)}
        className="bg-white border border-gray-300 rounded px-2 py-1 text-sm"
      >
        <option value="8">8px</option>
        <option value="10">10px</option>
        <option value="12">12px</option>
        <option value="14">14px</option>
        <option value="16">16px</option>
        <option value="18">18px</option>
        <option value="20">20px</option>
        <option value="22">22px</option>
        <option value="24">24px</option>
      </select>
      <div className="border-l border-gray-300 h-6 mx-2"></div>
      <select 
        onChange={(e) => handleFormatChange('fontStyle', e.target.value)}
        className="bg-white border border-gray-300 rounded px-2 py-1 text-sm"
      >
        <option value="normal">Normal</option>
        <option value="italic">Italic</option>
      </select>
      <select 
        onChange={(e) => handleFormatChange('textAlign', e.target.value)}
        className="bg-white border border-gray-300 rounded px-2 py-1 text-sm"
      >
        <option value="left">Align Left</option>
        <option value="center">Align Center</option>
        <option value="right">Align Right</option>
      </select>
    </div>
  );
};

export default Toolbar;
