// hooks/useSpreadsheet.js
import React, { createContext, useContext, useState, useCallback } from 'react';

const SpreadsheetContext = createContext();

export const useSpreadsheet = () => useContext(SpreadsheetContext);

export const SpreadsheetProvider = ({ children }) => {
  const [cells, setCells] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const cellsPerPage = 100; // Define how many cells per page

  const updateCell = useCallback((cellId, updates) => {
    setCells(prevCells => {
      const newCells = { ...prevCells, [cellId]: { ...prevCells[cellId], ...updates } };
      return newCells;
    });
  }, []);

  const formatCell = useCallback((property, value) => {
    setCells(prevCells => {
      const newCells = { ...prevCells };
      Object.keys(newCells).forEach(cellId => {
        if (newCells[cellId].selected) {
          newCells[cellId] = {
            ...newCells[cellId],
            style: { ...newCells[cellId].style, [property]: value }
          };
        }
      });
      return newCells;
    });
  }, []);

  const value = {
    cells,
    updateCell,
    formatCell,
    searchTerm,
    setSearchTerm,
    currentPage,
    setCurrentPage,
    cellsPerPage
  };

  return (
    <SpreadsheetContext.Provider value={value}>
      {children}
    </SpreadsheetContext.Provider>
  );
};
