import React from 'react';
import Cell from './Cell';
import { useSpreadsheet } from '../hooks/useSpreadsheet';

const Grid = () => {
  const { cells, updateCell, searchTerm } = useSpreadsheet();

  const filteredCells = cells.map((cell, index) => ({
    value: cell,
    index,
  })).filter(cell => cell.value.toLowerCase().includes(searchTerm.toLowerCase()));

  return (
    <div className="grid grid-cols-10 gap-0.5 p-4">
      {filteredCells.map(({ value, index }) => (
        <Cell
          key={index}
          value={value}
          onChange={(newValue) => updateCell(index, newValue)}
        />
      ))}
    </div>
  );
};

export default Grid;
