import React from 'react';
import { useSpreadsheet } from '../hooks/useSpreadsheet';

const FormatToolbar = () => {
  const { cells, updateCell } = useSpreadsheet();

  const handleAlignmentChange = (alignment) => {
    cells.forEach((cell, index) => {
      if (cell.selected) {
        updateCell(index, { format: { ...cell.format, alignment } });
      }
    });
  };

  const handleFontSizeChange = (fontSize) => {
    cells.forEach((cell, index) => {
      if (cell.selected) {
        updateCell(index, { format: { ...cell.format, fontSize } });
      }
    });
  };

  const handleValidationChange = (validation) => {
    cells.forEach((cell, index) => {
      if (cell.selected) {
        updateCell(index, { validation });
      }
    });
  };

  return (
    <div className="bg-gray-100 p-2 flex space-x-2">
      <button onClick={() => handleAlignmentChange('left')} className="bg-blue-500 text-white px-4 py-2 rounded">
        Align Left
      </button>
      <button onClick={() => handleAlignmentChange('center')} className="bg-blue-500 text-white px-4 py-2 rounded">
        Align Center
      </button>
      <button onClick={() => handleAlignmentChange('right')} className="bg-blue-500 text-white px-4 py-2 rounded">
        Align Right
      </button>
      <select onChange={(e) => handleFontSizeChange(Number(e.target.value))} className="bg-blue-500 text-white px-4 py-2 rounded">
        <option value={12}>12px</option>
        <option value={14}>14px</option>
        <option value={16}>16px</option>
        <option value={18}>18px</option>
      </select>
      <select onChange={(e) => handleValidationChange(e.target.value)} className="bg-blue-500 text-white px-4 py-2 rounded">
        <option value={null}>No Validation</option>
        <option value="numeric">Numeric Only</option>
      </select>
    </div>
  );
};
